import logo from "./logo.png";

const App=()=>{
  let a=1;
  let  newTime=new Date().toLocaleTimeString();
  console.log(newTime);

 const [time , ctime]=useState(newTime)

const updateTime=()=>{
  console.log("clicked")
     ctime(newTime);
}



  return(
    <div>
      <h1>{time}</h1>
      <button onClick={updateTime}>Update Time </button>
    </div>
  )
}







export default App;
